package a_practice_leo;

public class TestDicimal {

	 /**
	  * @param args
	  */
	 public static void main(String[] args) {
	  // TODO Auto-generated method stub
	  
	  int a=0;
	  int b=0;
	  
	  System.out.println("a의 변화 : "+a++);
	  System.out.println("b의 변화 : "+ ++b);
	  
	  
	  System.out.println("a의 두번째 변화 : "+a);
	 }

	}